      out.write("		function LoadImg(value, num){\r\n");
      out.write("			// 각각의 영역에 파일을 첨부 했을 경우 미리 보기가 가능하도록 하는 함수\r\n");
      out.write("				\r\n");
      out.write("			if(value.files && value.files[0]){\r\n");
      out.write("				var reader = new FileReader();//파일을 읽기 위함 함수\r\n");
      out.write("							\r\n");
      out.write("					reader.onload = function(e){								\r\n");
      out.write("					switch(num){\r\n");
      out.write("					case 1: \r\n");
      out.write("						$(\"#thumImg\").attr(\"src\", e.target.result);//타겟result:경로를 가져옴\r\n");
      out.write("						break;\r\n");
      out.write("					case 2:\r\n");
      out.write("						$(\"#imgs1\").attr(\"src\", e.target.result);\r\n");
      out.write("						break;\r\n");
      out.write("					case 3:\r\n");
      out.write("						$(\"#imgs2\").attr(\"src\", e.target.result);\r\n");
      out.write("						break;\r\n");
      out.write("					case 4:\r\n");
      out.write("						$(\"#imgs3\").attr(\"src\", e.target.result);\r\n");
      out.write("						break;\r\n");
      out.write("					\r\n");
      out.write("					}\r\n");
      out.write("				}\r\n");
      out.write("					//여러 사진을 선택하여 업로드 할 경우 처음선택한 사진만 업로드 됨(한 공간에는 한 사진만 올라감)			\r\n");
      out.write("				reader.readAsDataURL(value.files[0]);\r\n");
      out.write("			}\r\n");
      out.write("		}\r\n");
      out.write("	\r\n");
      out.write("	</script>\r\n");
